#!/bin/bash
# Example Development Session
# Generated by Claude Code Tmux Wizard

SESSION_NAME="dev-session"
PROJECT_PATH="/home/wv3/claude-code-work"

# Kill existing session if it exists
tmux kill-session -t $SESSION_NAME 2>/dev/null

# Create new session with first window
tmux new-session -d -s $SESSION_NAME -n "Claude" -c "$PROJECT_PATH"

# Window 0: Claude
tmux send-keys -t $SESSION_NAME:0 'claude' C-m

# Window 1: Blessed Monitor
tmux new-window -t $SESSION_NAME -n "Monitor" -c "$PROJECT_PATH"
tmux send-keys -t $SESSION_NAME:1 'cd apps/blessed-monitor && npm start' C-m

# Window 2: Server Logs
tmux new-window -t $SESSION_NAME -n "Logs" -c "$PROJECT_PATH"
tmux send-keys -t $SESSION_NAME:2 'tail -f logs/server.log' C-m

# Window 3: Ranger
tmux new-window -t $SESSION_NAME -n "Files" -c "$PROJECT_PATH"
tmux send-keys -t $SESSION_NAME:3 'ranger' C-m

# Enable mouse support
tmux set -g mouse on

# Attach to session
echo "âœ¨ Tmux session 'dev-session' created!"
echo ""
echo "Windows:"
echo "  0: Claude - Claude Code"
echo "  1: Monitor - Blessed Monitor"
echo "  2: Logs - Server Logs"
echo "  3: Files - Ranger File Manager"
echo ""
echo "Attaching to session..."
sleep 1

tmux attach-session -t $SESSION_NAME